mapper.update:
update credit
set remain = remain - #{cost}
where id = #{creditId}

update user_interface ui
set ui.invoke_count = ui.invoke_count+1
where ui.user_id=#{userId} and ui.interface_id = #{interfaceId}

mapper.select:
select cost
from interface_info
where   id= #{interfaceId}

select c.id creditId, c.sort_id,c.remain, u.id userId
from credit c
         left join user u on c.user_id = u.id
where u.access_key = #{accessKey}
  and c.secret_key = #{secretKey}

select count(*)
from user_interface
where user_id=#{userId} and interface_id=#{interfaceId}

select pom,example
from interface_info
where id = #{interfaceId}
